class Example1 {
  int field1,field2,field3;

  Example1(int field1) {
    this.field1 = field1;
    field2 = 0;
    foo(5); // OK, methods cannot be overlapped in java.
  }

  void method2(int i) {
    foo(i); // OK, methods cannot be overlapped in java.
  }

  void foo(int field3) {
    // violation below, reference to instance variable "field3" requires "this"
    field3 = field3;
  }
}

