  }
}

public class Example2 {

  public String textFun() {

    // violation below 'Opening quotes (""") of text-block must be on the new line'
    final String simpleScript = """
        s
      """; // violation 'Text-block quotes are not vertically aligned'

    // violation 2 lines below:
    //   'Opening quotes (""") of text-block must be on the new line'
    final String simpleScript2 = simpleScript +
        simpleScript + """
        this is simple script
        """; // violation 'Text-block quotes are not vertically aligned'

    // violation 2 lines below:
    //   'Opening quotes (""") of text-block must be on the new line'
    getData(
        1, """
           this is a multi-line message
           """
    );

    // violation below 'Opening quotes (""") of text-block must be on the new line'
    return """
        this is sample text
        """; // violation 'Text-block quotes are not vertically aligned'
  }

  public void getData(String text, int num) {}

  public void getData(int num, String text) {}

  public String getName() {
    return "this is function";
  }
}

public class Example3 {

  public String textFun() {
    final String simpleScript1 =
            """
            this is simple test""";
    // 2 violations above:
    //   'Closing quotes (""") of text-block must be on the new line.'
    //   'Text-block quotes are not vertically aligned'

    final String simpleScript3 = simpleScript1 +
        simpleScript1 +
        """
        this is simple script""";
    // 2 violations above:
    //   'Closing quotes (""") of text-block must be on the new line.'
    //   'Text-block quotes are not vertically aligned'

    String test1 = simpleScript3
        +
        """
        very good""".charAt(0) + getName();
    // 2 violations above:
    //   'Closing quotes (""") of text-block must be on the new line.'
    //   'Text-block quotes are not vertically aligned'

    getData(
        """
        Hello,
        This is a multi-line message."""
    ); // 2 violations above:
    //   'Closing quotes (""") of text-block must be on the new line.'
    //   'Text-block quotes are not vertically aligned'

    return
        """
        THE MULTI-LINE MESSAGE""";
    // 2 violations above:
    //   'Closing quotes (""") of text-block must be on the new line.'
    //   'Text-block quotes are not vertically aligned'
  }

  public void getData(String text, int num) {}
  public void getData(String text) {}

  public String getName() {
    return "this is function";
  }
}

