public class Example1 {

  public static String textFun() {

    final String simpleScript =
        """
        s
        """;

    final String simpleScript1 =
        """
        this is simple test;
        """;

    String stringFollowedBy =
        """
          Hello there
        """ + getName();

    getData(
        """
        Hello,
        This is a multi-line message.
        """, 0);

    return
        """
        this is sample text
        """;
  }
}

public class Example2 {

  public String textFun2() {
    // violation below, should be on new line.
    final String simpleScript2 = """
        this is sample text
        """;

      getData(
          """
          Hello,
          This is a multi-line message.""" // violation, should be on new line.
      );

      return
          """
            THE MULTI-LINE MESSAGE """; // violation, should be on new line.
  }
}

public class Example3 {
  public void textFun4() {

    // OK
    String e1 =
        """
      content of the block. e1
        """;

    // According to rule following is OK.
    String e2 =
"""
         content of the block e2
""";

    String e3 =
        """
        content of the block of e3
            """;
    // violation above, 'Text-block quotes are not vertically aligned. '

    getData(
      """
        Indentation of Text-block
      """,
      5
    );
  }
}
