class Example1 {
  // violation below, 'interfaces should describe a type and hence have methods.'
  interface Test1 {
    int a = 3;
  }

  // ok below, because marker interfaces are allowed by default.
  interface Test2 {

  }

  interface Test3 { // ok, because it has a method.
    int a = 3;
    void test();
  }
}

class Example2 {
  // violation below, 'interfaces should describe a type and hence have methods.'
  interface Test1 {
    int a = 3;
  }

  // violation below, 'interfaces should describe a type and hence have methods.'
  interface Test2 {

  }

  interface Test3 { // ok, because it has a method.
    int a = 3;
    void test();
  }
}
