class Example4 {
  void testMethod1() {
    for (int i = 0; i != 10; i++) {
      System.out.println(i);
    }
    int k = 0;
    for (; ; ) { // violation, 'Count of 0 for 'FOR_CONDITION' descendant'
      System.out.println(k);
    }
  }

  void testMethod2() {
    int[] array = new int[] {1, 2, 3, 4, 5};
    for (int i = 0; i != array.length; i++) {
      System.out.println(i);
    }
    int j = 0;

    for (; j != array.length;) {
      System.out.println(j);
      j++;
    }
  }
}

