public class Example1 {
  @SuppressWarnings("memberName")
  int J; // ok only because there is annotation
  int JJ; // violation 'must match pattern'

  @SuppressWarnings({"MemberName", "NoWhitespaceAfter"})
  int [] ARRAY; // ok only because there is annotation for both Checks
  int [] ARRAY2;
  // 2 violations above:
  //   ''int' is followed by whitespace'
  //   'must match pattern'
}

package com.puppycrawl.tools.checkstyle.filters.suppresswarningsfilter;

public class Example2 {
  @SuppressWarnings("checkstyle:systemout")
  public static void foo() {
    System.out.println("Debug info."); // ok, because there is annotation
  }
  public static void boo() {
    System.out.println("Some info."); // violation 'use SLF4J instead.'
  }
}
