class CyclomaticComplexity {
  // Cyclomatic Complexity = 11
  int a, b, c, d, n;
  public void foo() { // 1, function declaration
    if (a == 1) { // 2, if
      fun1();
    } else if (a == b // 3, if
      && a == c) { // 4, && operator
      if (c == 2) { // 5, if
        fun2();
      }
    } else if (a == d) { // 6, if
      try {
        fun4();
      } catch (Exception e) { // 7, catch
      }
    } else {
      switch(n) {
        case 1: // 8, case
          fun1();
          break;
        case 2: // 9, case
          fun2();
          break;
        case 3: // 10, case
          fun3();
          break;
        default:
          break;
      }
    }
    d = a < 0 ? -1 : 1; // 11, ternary operator
  }
}

class CyclomaticComplexity {
  // Cyclomatic Complexity = 5
  int a, b, c, d;
  public void foo() { // 1, function declaration
    while (a < b // 2, while
      && a > c) {
      fun();
    }
    if (a == b) {
      do { // 3, do
        fun();
      } while (d);
    } else if (c == d) {
      while (c > 0) { // 4, while
        fun();
      }
      do { // 5, do-while
        fun();
      } while (a);
    }
  }
}

class CyclomaticComplexity {
  // Cyclomatic Complexity = 11
  int a, b, c, d, e, n;
  public void foo() { // 1, function declaration
    if (a == b) { // 2, if
      fun1();
    } else if (a == 0 // 3, if
      && b == c) { // 4, && operator
      if (c == -1) { // 5, if
        fun2();
      }
    } else if (a == c // 6, if
      || a == d) { // 7, || operator
      fun3();
    } else if (d == e) { // 8, if
      try {
        fun4();
      } catch (Exception e) { // 9, catch
      }
    } else {
      switch(n) { // 10, switch
        case 1:
          fun1();
          break;
        case 2:
          fun2();
          break;
        default:
          break;
      }
    }
    a = a > 0 ? b : c; // 11, ternary operator
  }
}
