subsection.source:
public class MySingleton
{
    private static theInstance = null;

    private MySingleton() {}

    public MySingleton getInstance() {
        if ( theInstance == null ) { // synchronize only if necessary
            synchronized( MySingleton.class ) {
                if ( theInstance == null ) {
                    theInstance = new MySingleton();
                }
            }
        }
    }
}

