subsection.source:
switch (i){
case 0:
    i++; // fall through

case 1:
    i++;
    // falls through
case 2:
case 3:
case 4: {
    i++;
}
// fallthrough
case 5:
    i++;
/* fallthru */case 6:
    i++
    break;
}

