a = a + b;
b = a + a;
count = b; // DECLARATION OF VARIABLE 'count'
           // SHOULD BE HERE (distance = 3)

int count;
{
     a = a + b;
     count = b; // DECLARATION OF VARIABLE 'count'
                // SHOULD BE HERE (distance = 2)
}

