  public enum Colors {
    RED, GREEN, YELLOW;

    public String getRGB() { ... } // NOT counted towards ExampleClass
  }

  public void example() { // counted towards ExampleClass
    Runnable r = (new Runnable() {
      public void run() { ... } // NOT counted towards ExampleClass, won't produce any violations
    });
  }

  public static class InnerExampleClass {
    protected void example2() { ... } // NOT counted towards ExampleClass,
                                   // but counted towards InnerExampleClass
  }
}

public class MyClass {
  public MyClass() {  // constructor (line 1)
      /* line 2
          ...
         line 150 */
  } // line 151, violation, as it is over 150

  public void firstExample() { // line 1

      // line 3
      System.out.println("line 4");
      /* line 5
         line 6 */
  } // line 7, OK, as it is less than 150

  public void secondExample() { // line 1
      // line 2
      System.out.println("line 3");

      /* line 5
          ...
         line 150 */
  } // line 151, violation, as it is over 150
}

public class MyTest {
  public MyTest()  {          // constructor (line 1)
      int var1 = 2;           // line 2
      int var2 = 4;           // line 3
      int sum = var1 + var2;  // line 4
  } // line 5, OK, constructor is not mentioned in the tokens

  public void firstMethod() { // line 1
      // comment (line 2)
      System.out.println("line 3");
  } // line 4, OK, as it allows at most 4 lines

  public void secondMethod() { // line 1
      int index = 0;   // line 2
      if (index < 5) { // line 3
          index++;     // line 4
      }                // line 5
  } // line 6, violation, as it is over 4 lines

  public void thirdMethod() { // line 1

      // comment (line 3)
      System.out.println("line 4");
  } // line 5, violation, as it is over 4 lines
}

